  z-index: $z-index-nav;

  @include desktop {
    padding-top: 60px;
  }

  .nav__container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 100%;

    .logo__img {
      width: 80px;
    }

    @include desktop {
      .logo__img {
        width: 160px;
      }
    }

    .nav__links {
      display: flex;

      li {
        margin-left: 1.5rem;
        font-size: var(--h3);
        font-family: $heading-font;
        font-weight: bold;

        a {
          color: var(--white);

          &:hover {
            text-decoration: underline;
            color: var(--cyan);
          }
        }
      }

      @include desktop {
        li {
          margin-left: 3rem;
        }
      }

      li:first-child {
        margin-left: 0;
      }
    }
  }
}

