/* ========== INTRO SECTION ========== */
#intro {
  background-color: var(--primary-color-light);
  position: relative;

  .intro__bg {
    position: absolute;
    bottom: 40%;
    width: 100%;
  }

  .fill__bg {
    position: absolute;
    top: 58%;
    background-color: var(--primary-color);
    width: 100%;
    height: 42%;
  }

  padding-top: $nav-height;
  padding-bottom: $nav-height;

  .intro__container {
    gap: 2rem;
    position: relative;
    z-index: 5;

    .img__container {
      width: 90%;
      margin: auto;
      margin-top: 2rem;
      text-align: center;
    }

    .intro__data {
      text-align: center;

      .intro__title {
        font-family: $heading-font;
        font-size: var(--h1);
      }

      .intro__paragraph {
        font-size: var(--normal-font-size);
        padding: 1rem 1.25rem;
      }
    }
  }
}

@include tablet {
  #intro {
    .intro__bg {
      bottom: 20%;
      width: 100%;
    }
    .fill__bg {
      top: 80%;
      height: 20%;
    }

    .intro__container {
      max-width: $tablet;
      margin: auto;
    }
  }
}

@include desktop {
  #intro {
    padding-top: 1rem;

    .intro__bg {
      bottom: -4%;
      // visibility: hidden;
    }
    .fill__bg {
      top: 0;
      height: 0;
    }

    .intro__container {
      gap: 1rem;
      padding-top: 3rem;
      max-width: $desktop;

      .img__container img {
        width: 50%;
      }

      .intro__data {
        max-width: 45rem;
        margin: auto;

        .intro__paragraph {
          padding: 1rem 3rem;
          font-size: 1.3125rem;
        }
      }
    }
  }
}

sources.assets/scss/sections/_features.scss:
/* ========== FEATURES SECTION ========== */
.features__container {
  gap: 3rem;

  .single__feature {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin: 1rem;

    .feature__heading {
