sources.assets/scss/sections/_team.scss:
/* ========== TEAM SECTION ========== */
.team__container {
  // margin: 5rem auto;

  .team__img__container {
    padding: 2rem 1rem;
  }

  .team__data {
    .team__heading {
      font-size: var(--h2);
      font-weight: $font-bold;
      font-family: $heading-font;

      text-align: center;
    }

    .team__paragraph {
      font-size: var(--normal-font-size);
      font-family: $body-font;

      margin: 1rem;
      margin-bottom: 0;
    }

    .team__link {
      font-size: var(--small-font-size);
      color: var(--cyan);
      margin: auto 1rem;
      padding: 0.25rem 0;
      border-bottom: 1px solid var(--cyan);
      transition: all 0.5s;

      .team__link__arrow {
        vertical-align: middle;
      }

      &:hover {
        color: var(--white);
        border-bottom: 1px solid var(--white);
      }
    }
  }
}

@include laptop {
  .team__container {
    grid-template-columns: 1fr 1fr;

    .team__data {
      align-self: center;
      .team__heading {
        text-align: left;

        span {
          display: block;
        }
      }

      .team__paragraph {
        margin: 1rem 0;
      }

      .team__link {
        margin: 0;
      }
    }
  }
}

@include desktop {
  .team__container {
    grid-template-columns: 1fr 1fr;

    .team__data .team__heading {
      font-size: var(--h1);
    }
  }
}

sources.assets/scss/sections/_testimonial.scss:
/* ========== TESTIMONIAL SECTION ========== */
.testi__container {
  padding: 1rem;
  gap: 2rem;

  .card {
    background-color: var(--dark-blue);
    border-radius: 0.5rem;
    padding: 1.75rem;
    line-height: 1rem;

    .testi__text {
      font-size: var(--smaller-font-size);
      letter-spacing: 1px;
      margin-bottom: 1rem;
    }

    .profile {
      display: flex;
      align-items: center;

      .profile__avatar {
        width: 13%;

        img {
          border-radius: 50%;
        }
      }

      .profile__info {
        margin-left: 1rem;
        h3 {
          font-size: var(--h3);
        }
        span {
          font-size: var(--tiny-font-size);
        }
      }
    }
  }

  .first__card {
    position: relative;

    &::before {
      content: "";
      position: absolute;
      top: -9%;
      left: 2%;
      background-image: url(../img/bg-quotes.png);
      background-repeat: no-repeat;
      background-size: contain;
      width: 20px;
      height: 20px;
    }
  }
}

@include laptop {
  .testi__container {
    grid-template-columns: 1fr 1fr 1fr 1fr;

    .card:nth-child(1) {
      grid-column: 1 / 3;
    }
    .card:nth-child(2) {
      grid-column: 3 / 5;
    }
    .card:nth-child(3) {
      grid-column: 2 / 4;
    }

    .first__card {
      &::before {
        top: -10%;
        left: -2%;
        width: 30px;
        height: 30px;
      }
    }
  }
}

@include desktop {
  .testi__container {
    grid-template-columns: 1fr 1fr 1fr;

    .card {
      grid-column: auto !important;
    }

    .first__card {
      &::before {
        top: -20%;
        left: -2%;
        width: 55px;
        height: 55px;
      }
    }
  }
}

sources.assets/scss/sections/_signin.scss:
/* ========== SIGN IN SECTION ========== */
#signin {
  background: linear-gradient(
    to bottom,
    var(--primary-color) 50%,
    var(--primary-color-dark) 50%
  );
  padding-bottom: 0;

  .signin__container {
    .signin__card {
      background-color: var(--primary-color-light);
      padding: 2.5rem 2rem;
      border-radius: 0.75rem;

      h2 {
        font-size: var(--h2);
        text-align: center;
      }

      p {
        text-align: center;
        margin: 1rem 0;
      }

      .input__container {
        display: grid;

        .email__container {
          width: 100%;
          background: var(--white);
          padding: 0.5rem 1rem;
          border-radius: 2rem;

          .email__input {
            width: 100%;
            outline: none;
            border: none;
          }
        }

        .email__btn {
          width: 100%;
        }
      }
    }
  }
}

@include laptop {
  #signin .signin__container {
    .signin__card {
      max-width: $laptop;
      // width: 60%;
      margin: auto;
      padding: 2.5rem 3.5rem;

      .input__container {
        grid-template-columns: 2fr 1fr;
        gap: 2rem;

        .email__container {
          padding: 1rem 2rem;
        }

        .email__btn {
          margin: 0;
          padding: 1.125rem;
        }
      }
    }
  }
}

sources.assets/scss/sections/_footer.scss:
/* ========== FOOTER ========== */
#footer {
  background-color: var(--primary-color-dark);
  padding-bottom: 1rem;
  .footer__container {
    font-size: var(--big-font-size);
    padding: 1rem;
    gap: 0;

    .footer__child {
      .footer__link {
        &:hover {
          font-weight: $font-bold;
        }
      }

      .social__icon {
        width: 100%;
        fill: var(--white);
      }
    }

    .footer__info {
      margin-top: 1rem;
      .logo {
        margin-bottom: 1rem;
      }
    }

    .phone_email_footer {
      margin-bottom: 1rem;
    }

    .footer_icon_info {
      display: grid;
      grid-template-columns: 2.25rem 1fr;
      margin-bottom: 1rem;

      img {
        margin-top: 0.5rem;
      }
    }

    .footer__nav {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      margin: 1rem 0;

      a {
        color: var(--white);
        margin: 0.5rem 0;
      }
    }

    .footer__social {
      display: flex;
      justify-content: center;
      margin-top: 2rem;

      a {
        border: 1px solid white;
        border-radius: 50%;
        width: 2rem;
        height: 2rem;
        padding: 4px;
        margin: 0.5rem;

        &:hover {
          border: 1px solid var(--cyan);
        }

        &:hover .social__icon {
          fill: var(--cyan);
        }

        img {
          width: 100%;
        }
      }
    }
  }
}

@include laptop {
  #footer {
    .footer__container {
      grid-template-columns: 2fr 1fr 1fr;
      // gap: 3rem;
      column-gap: 3rem;

      .footer__child:nth-child(2) {
        grid-column: 1 / 2;
      }
      .footer__child:nth-child(3) {
        grid-row: 1 / 2;
        grid-column: 2 / 3;
      }
      .footer__child:nth-child(4) {
        grid-row: 1 / 2;
        grid-column: 3 / 4;
      }
      .footer__child:nth-child(5) {
        grid-row: 3 / 4;
        grid-column: 1 / 4;
      }
    }
  }
}

@include desktop {
  #footer {
    .footer__container {
      grid-template-columns: 3fr 2fr 1fr 1fr 1fr;
      gap: 3rem;

      .footer__child:nth-child(2) {
        grid-row: 1 / 2;
        grid-column: 2 / 3;
        margin-top: 2rem;
      }
      .footer__child:nth-child(3) {
        grid-row: 1 / 2;
        grid-column: 3 / 4;
      }
      .footer__child:nth-child(4) {
        grid-row: 1 / 2;
        grid-column: 4 / 5;
      }
      .footer__child:nth-child(5) {
        grid-row: 1 / 2;
        grid-column: 5 / 6;
        margin-top: 1rem;
      }
    }
  }
}
